INSERT INTO `mstx_step` (`sid`, `mid`, `simg`, `explain`, `sort`) VALUES
(1, 1, '201308072133141375923838.jpg', '鸡肉洗净带骨剁成小块，充分洗去血水，洋葱去皮切小块，姜切片，红辣椒切成斜段，大蒜去外皮备用，新鲜九层塔洗净', 1),
(2, 1, '201308072133331376037277.jpg', ' 2烧热油锅，倒入2/3杯麻油 植物油，大蒜，姜片，辣椒粒一起爆香', 2),
(3, 1, '201308072133471376286658.jpg', '倒入洋葱片炒出香味', 3),
(4, 1, '201308072134051375928002.jpg', '鸡块倒入锅中翻炒，至鸡肉表面微微上色，撒入少许精盐', 4),
(5, 1, '201308072134261376091776.jpg', '加入1杯生抽 老抽，炒匀上色', 5),
(6, 1, '201308072134381376071742.jpg', '再加1杯米酒', 6),
(7, 1, '201308072134521376803651.jpg', '调入其余1/3杯麻油 植物油，炒匀后加入冰糖融化', 7),
(8, 1, '201308072135071376494177.jpg', '煸炒至鸡肉完全上色入味时倒入新鲜九层塔', 8),
(9, 1, '201308072135311376115691.jpg', '转小火至汤汁浓稠，翻炒使汤汁包裹在鸡肉上,盛出装盘即可食用', 9),
(10, 21, '201310202111491383140002.jpg', '将虾洗净去皮去虾线。', 1),
(11, 21, '201310202112091382997502.jpg', '将葱剁成葱末，娃娃菜切块，也可以用手撕。', 2),
(12, 21, '201310202112311382403172.jpg', '锅内烧热油，倒入葱末煸香。', 3),
